import React, { Component } from "react";
import { Card, InputGroup, Button,Form} from "react-bootstrap";
import "./all.css";
export default class SearchBar extends Component {
    constructor(props) {
        super(props);
        this.state = { 
          value: ""
        }

      }
      handlechange = (e) => {
        this.setState({ value: e.target.value })
        // console.log("here is value",this.state.value)
        if(this.state.value === ""){
            return console.log('enter the search content first ');
         }

      }
      callApi=()=>{

        fetch(`http://localhost:3001/Videoid?search=${this.state.value}`).then((response)=>{
        response.json().then((data) => {

            if (data.error) {
                return console.log('error occured',data.error)
            }
            console.log(data)
        })

        })
        this.callApi()


        }
  render() {

    return (

    <div className="topMargin leftmargin ">

          <Card border="light" style={{ width: '50rem' }}>
            <Card.Body>
              <InputGroup className="mb-3 ">
              <Form.Control
               type="Text" 
               placeholder="Search"
               value={this.state.value} 
               onChange={this.handlechange} />
                <InputGroup.Append>
                  <Button variant="outline-info" id="searchBtn">
                    Search
                  </Button>
                </InputGroup.Append>
              </InputGroup>
            </Card.Body>
          </Card>
        </div>

    );

  }

}
