import React, { Component } from "react";
import { Card, InputGroup, Button, Form,Image } from "react-bootstrap";
import "./all.css";
import showVideos from "./showVideos";
export default class SearchBar extends Component {
  constructor(props) {
    super(props);
    this.state = {
      value: "",
      isLoad: false,
    };
  }
  handlechange = (event) => {
    // console.log("here is value",this.state.value)
    // if(this.state.value === ""){
    //     return console.log('enter the search content first ');
    //  }
    this.setState({ value: event.target.value });
    console.log(event.target.value);
    console.log(this.state.value);
  };
  handleForm = (e) => {
    e.preventDefault();
    const { value, isLoad } = this.state;
    //const letters = /^[A-Za-z]+$/;
    const inputval = value;

    if (inputval) {
      this.callApi();
      this.setState({ isLoad: true });

      console.log("input value", inputval);
    } else {
      e.target[0].className = "red";
    }
  };

  callApi = () => {
    fetch(`http://localhost:3001/Videoid?search=${this.state.value}`).then(
      (response) => {
        response.json().then((data) => {
          if (data.error) {
            return console.log("error occured", data.error);
