      return(
        <div>
        <showVideos Obj={this.state}/>
    </div>
      )

    }
    return (
      <div className="topMargin leftmargin ">
        <Card border="light" style={{ width: "50rem" }}>
          <Card.Body>
            <Form onSubmit={this.handleForm}>
              <InputGroup className="mb-3 ">
                <Form.Control
                  type="Text"
                  placeholder="Search"
                  value={this.state.value}
                  onChange={this.handlechange}
                />
                <InputGroup.Append>
                  <Button variant="outline-info" type="submit">
                    Search
                  </Button>
                </InputGroup.Append>
              </InputGroup>
            </Form>
          </Card.Body>
        </Card>
      </div>
    );
  }
}
